subtitles.code:
.block {
	display: block;
	background-color: white;
	border: 1px solid #333;
	padding: 10px;
	margin: 5px;
	height: 60px;
}

.inline {
	display: inline;
	background-color: teal;
	border: 1px solid #333;
	padding: 10px;
	margin: 5px;
	height: 60px;
}

.inline-block {
	display: inline-block;
	width: 150px;
	height: 50px;
	background-color: #f0f0f0;
	border: 1px solid #333;
	padding: 10px;
	margin: 5px;
}
