subtitles.code:
.box {
	transform: translate(50px, 50px);
	transform: rotate(45deg);
	transform: scale(1.5);
	transform: skew(20deg, 10deg);
	}

.translate-example {
	width: 100px; 
	height: 100px;
	background-color: lightblue;
	transform: translate(50px, 50px); /* 요소를 오른쪽 50px, 아래쪽 50px 이동 */
	}

.scale-example {
	width: 100px;
	height: 100px;
	background-color: lightcoral;
	transform: scale(1.5); /* 요소를 1.5배 확대 */
	}

.rotate-example {
	width: 100px;
height: 100px;
 background-color: lightgreen; 
transform: rotate(45deg); /* 요소를 시계 방향으로 45도 회전 */
}

 .skew-example {
	 width: 100px; 
	height: 100px;
	background-color: lightgoldenrodyellow; 
	transform: skew(20deg, 10deg); /* X축으로 20도, Y축으로 10도 기울이기 */
	}
