.box {
	 width: 100px; 
height: 100px;
background: coral;
transition: opacity 0.5s, transform 0.5s;
}
.box:hover {
	opacity: 0.5; 
transform: translateX(50px);
}

.property-example {
	 width: 100px;
 height: 100px; 
background-color: lightblue; 
transition-property: background-color; /* 배경색만 전환 효과 적용 */ 
transition-duration: 0.5s; 
} 
 .property-example:hover { 
	background-color: lightcoral;
}

.duration-example {
	 width: 100px;
height: 100px; 
background-color: lightgreen; 
transition-property: background-color; 
transition-duration: 2s; /* 2초 동안 전환 */
} 
.duration-example:hover { 
background-color: lightgoldenrodyellow; 
 }

.timing-example {
	width: 100px;
height: 100px;
background-color: lightpink; 
transition-property: transform; 
transition-duration: 1s;  transition-timing-function: ease-in-out; /* ease-in-out 효과 *} .timing-example:hover { transform: translateX(100px); }

.delay-example {
	 width: 100px; 
height: 100px; 
background-color: lightsteelblue; 
transition-property: background-color; 
transition-duration: 1s;  
transition-delay: 0.5s; /* 0.5초 지연 후 전환 */
} 
.delay-example:hover { 
	background-color: lightcoral;
}
