.element { 
     transform-style: preserve-3d; /* 3D 변형 사용 설정 */ 
    transform: translateZ(50px) rotateZ(45deg) scaleZ(1.5); 
    width: 200px; 
    height: 200px; 
    background-color: #3498db;
    }

.container {    
perspective: 500px;
    }
.box {
    width: 150px;
    height: 150px;
    background: lightblue;
    transform: rotateZ(30deg) translateZ(50px);
    transition: transform 1s;
    }
.box:hover {
    transform: rotateZ(60deg) translateZ(100px) scaleZ(1.2);
    }
